<?phpadd_shortcode('link_buttons', 'shortcode_link_buttons');function shortcode_link_buttons($atts, $content = null) {    $text = get_option("link_text");    $link1 = get_option(SHORT_NAME . "_link1");    $link2 = get_option(SHORT_NAME . "_link2");    $link3 = get_option(SHORT_NAME . "_link3");    $link4 = get_option(SHORT_NAME . "_link4");    $link5 = get_option(SHORT_NAME . "_link5");    $html = <<<HTML<p style="text-align: center;">{$text}</p><div class="link_buttons">    <a href="{$link1}">Link 1</a>    <a href="{$link2}">Link 2</a>    <a href="{$link3}">Link 3</a>    <a href="{$link4}">Link 4</a>    <a href="{$link5}">Link 5</a></div>HTML;    return $html;}//////////////////////////////////////////////////////////////////// Add buttons to tinyMCE//////////////////////////////////////////////////////////////////add_action('init', 'add_button');function add_button() {    if (current_user_can('edit_posts') && current_user_can('edit_pages')) {        add_filter('mce_external_plugins', 'add_plugin');        add_filter('mce_buttons_3', 'register_button');    }}function register_button($buttons) {    array_push($buttons, "link_buttons");    return $buttons;}function add_plugin($plugin_array) {    $plugin_array['link_buttons'] = get_template_directory_uri() . '/libs/js/tinymce.js';    return $plugin_array;}